import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  loginSchema, 
  registerSchema, 
  insertLessonSchema,
  insertQuizResultSchema,
  insertContactMessageSchema 
} from "@shared/schema";
import bcrypt from "bcryptjs";
import jwt from "jsonwebtoken";

const JWT_SECRET = process.env.JWT_SECRET || "your-secret-key";

// Auth middleware
const authenticateToken = async (req: any, res: any, next: any) => {
  const authHeader = req.headers.authorization;
  const token = authHeader && authHeader.split(' ')[1];

  if (!token) {
    return res.status(401).json({ message: "Access token required" });
  }

  try {
    const decoded = jwt.verify(token, JWT_SECRET) as any;
    const user = await storage.getUser(decoded.userId);
    if (!user) {
      return res.status(401).json({ message: "User not found" });
    }
    req.user = user;
    next();
  } catch (error) {
    return res.status(403).json({ message: "Invalid token" });
  }
};

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth routes
  app.post("/api/auth/register", async (req, res) => {
    try {
      const userData = registerSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(userData.email);
      if (existingUser) {
        return res.status(400).json({ message: "User already exists with this email" });
      }

      const existingUsername = await storage.getUserByUsername(userData.username);
      if (existingUsername) {
        return res.status(400).json({ message: "Username already taken" });
      }

      const { confirmPassword, ...userToCreate } = userData;
      const user = await storage.createUser(userToCreate);

      const token = jwt.sign({ userId: user.id }, JWT_SECRET, { expiresIn: '7d' });

      const { password, ...userWithoutPassword } = user;
      res.json({ user: userWithoutPassword, token });
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = loginSchema.parse(req.body);

      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const isValidPassword = await bcrypt.compare(password, user.password);
      if (!isValidPassword) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const token = jwt.sign({ userId: user.id }, JWT_SECRET, { expiresIn: '7d' });

      const { password: _, ...userWithoutPassword } = user;
      res.json({ user: userWithoutPassword, token });
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.get("/api/auth/me", authenticateToken, async (req: any, res) => {
    const { password, ...userWithoutPassword } = req.user;
    res.json({ user: userWithoutPassword });
  });

  // Quiz routes
  app.get("/api/quizzes", async (req, res) => {
    try {
      const quizzes = await storage.getQuizzes();
      res.json(quizzes);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.get("/api/quizzes/:id", async (req, res) => {
    try {
      const quiz = await storage.getQuiz(req.params.id);
      if (!quiz) {
        return res.status(404).json({ message: "Quiz not found" });
      }
      res.json(quiz);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.post("/api/quiz-results", authenticateToken, async (req: any, res) => {
    try {
      const resultData = insertQuizResultSchema.parse({
        ...req.body,
        userId: req.user.id,
      });

      const result = await storage.createQuizResult(resultData);
      res.json(result);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  app.get("/api/quiz-results", authenticateToken, async (req: any, res) => {
    try {
      const results = await storage.getQuizResults(req.user.id);
      res.json(results);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Lesson routes
  app.get("/api/lessons", authenticateToken, async (req: any, res) => {
    try {
      const lessons = await storage.getLessons(req.user.id);
      res.json(lessons);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  app.post("/api/lessons", authenticateToken, async (req: any, res) => {
    try {
      const lessonData = insertLessonSchema.parse({
        ...req.body,
        studentId: req.user.id,
      });

      const lesson = await storage.createLesson(lessonData);
      res.json(lesson);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Resource routes
  app.get("/api/resources", async (req, res) => {
    try {
      const resources = await storage.getResources();
      res.json(resources);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Forum routes
  app.get("/api/forum", async (req, res) => {
    try {
      const posts = await storage.getForumPosts();
      res.json(posts);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Contact routes
  app.post("/api/contact", async (req, res) => {
    try {
      const messageData = insertContactMessageSchema.parse(req.body);
      const message = await storage.createContactMessage(messageData);
      res.json(message);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type Quiz, 
  type InsertQuiz,
  type QuizResult,
  type InsertQuizResult,
  type Lesson,
  type InsertLesson,
  type Resource,
  type InsertResource,
  type ForumPost,
  type InsertForumPost,
  type ContactMessage,
  type InsertContactMessage,
  users,
  quizzes,
  quizResults,
  lessons,
  resources,
  forumPosts,
  contactMessages
} from "@shared/schema";
import { randomUUID } from "crypto";
import bcrypt from "bcryptjs";
import { drizzle } from "drizzle-orm/neon-http";
import { neon } from "@neondatabase/serverless";
import { eq } from "drizzle-orm";
import fs from "fs";
import path from "path";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Quiz operations
  getQuizzes(): Promise<Quiz[]>;
  getQuiz(id: string): Promise<Quiz | undefined>;
  createQuiz(quiz: InsertQuiz): Promise<Quiz>;

  // Quiz result operations
  getQuizResults(userId: string): Promise<QuizResult[]>;
  createQuizResult(result: InsertQuizResult): Promise<QuizResult>;

  // Lesson operations
  getLessons(userId: string): Promise<Lesson[]>;
  createLesson(lesson: InsertLesson): Promise<Lesson>;
  updateLesson(id: string, updates: Partial<Lesson>): Promise<Lesson | undefined>;

  // Resource operations
  getResources(): Promise<Resource[]>;
  createResource(resource: InsertResource): Promise<Resource>;

  // Forum operations
  getForumPosts(): Promise<ForumPost[]>;
  createForumPost(post: InsertForumPost): Promise<ForumPost>;

  // Contact operations
  createContactMessage(message: InsertContactMessage): Promise<ContactMessage>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private quizzes: Map<string, Quiz>;
  private quizResults: Map<string, QuizResult>;
  private lessons: Map<string, Lesson>;
  private resources: Map<string, Resource>;
  private forumPosts: Map<string, ForumPost>;
  private contactMessages: Map<string, ContactMessage>;

  constructor() {
    this.users = new Map();
    this.quizzes = new Map();
    this.quizResults = new Map();
    this.lessons = new Map();
    this.resources = new Map();
    this.forumPosts = new Map();
    this.contactMessages = new Map();

    this.seedData();
  }

  private async seedData() {
    // Create default teacher
    const teacherId = randomUUID();
    const teacher: User = {
      id: teacherId,
      username: "teacher",
      email: "teacher@grammarmaster.com",
      password: await bcrypt.hash("password", 10),
      firstName: "John",
      lastName: "Smith",
      role: "teacher",
      avatar: null,
      createdAt: new Date(),
    };
    this.users.set(teacherId, teacher);

    // Create sample quizzes
    const quizzes = [
      {
        id: randomUUID(),
        title: "Present Tenses",
        description: "Master the use of simple present, present continuous, and present perfect tenses.",
        difficulty: "beginner",
        category: "tenses",
        timeLimit: 10,
        questions: [
          {
            id: 1,
            question: "She _______ to work every morning at 8 AM.",
            options: ["go", "goes", "going", "went"],
            correctAnswer: 1,
            explanation: "With third person singular (she/he/it) in simple present, we add 's' to the verb."
          },
          {
            id: 2,
            question: "They _______ studying English for two years.",
            options: ["are", "have been", "were", "had been"],
            correctAnswer: 1,
            explanation: "Present perfect continuous shows an action that started in the past and continues to the present."
          }
        ],
        createdAt: new Date(),
      },
      {
        id: randomUUID(),
        title: "Conditional Sentences",
        description: "Learn about zero, first, second, and third conditional structures.",
        difficulty: "intermediate",
        category: "conditionals",
        timeLimit: 15,
        questions: [
          {
            id: 1,
            question: "If it _______ tomorrow, we will stay inside.",
            options: ["rain", "rains", "will rain", "rained"],
            correctAnswer: 1,
            explanation: "In first conditional, we use simple present in the if-clause and will + infinitive in the main clause."
          }
        ],
        createdAt: new Date(),
      },
      {
        id: randomUUID(),
        title: "Advanced Grammar",
        description: "Challenge yourself with complex grammatical structures and rules.",
        difficulty: "advanced",
        category: "advanced",
        timeLimit: 20,
        questions: [
          {
            id: 1,
            question: "_______ the meeting been postponed, we would have had more time to prepare.",
            options: ["Had", "If", "Should", "Were"],
            correctAnswer: 0,
            explanation: "This is an inverted third conditional structure, where 'had' is moved to the beginning instead of using 'if'."
          }
        ],
        createdAt: new Date(),
      }
    ];

    quizzes.forEach(quiz => this.quizzes.set(quiz.id, quiz));

    // Create sample resources
    const resources = [
      {
        id: randomUUID(),
        title: "Complete Tenses Guide",
        description: "Comprehensive guide covering all English tenses with examples and usage rules.",
        type: "pdf",
        category: "grammar-guide",
        url: "/resources/tenses-guide.pdf",
        isPremium: false,
        fileSize: 2048576, // 2MB
        duration: null,
        createdAt: new Date(),
      },
      {
        id: randomUUID(),
        title: "Mastering Conditionals",
        description: "Step-by-step video explanation of all conditional sentence types with practical examples.",
        type: "video",
        category: "video-lesson",
        url: "/resources/conditionals-video.mp4",
        isPremium: true,
        fileSize: 52428800, // 50MB
        duration: 2700, // 45 minutes
        createdAt: new Date(),
      },
      {
        id: randomUUID(),
        title: "Article Usage Practice",
        description: "Practice exercises for mastering the correct usage of articles (a, an, the).",
        type: "worksheet",
        category: "practice",
        url: "/resources/articles-worksheet.pdf",
        isPremium: false,
        fileSize: 1048576, // 1MB
        duration: null,
        createdAt: new Date(),
      }
    ];

    resources.forEach(resource => this.resources.set(resource.id, resource));
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.username === username);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const hashedPassword = await bcrypt.hash(insertUser.password, 10);
    const user: User = { 
      ...insertUser, 
      id, 
      password: hashedPassword,
      createdAt: new Date()
    };
    this.users.set(id, user);
    return user;
  }

  async getQuizzes(): Promise<Quiz[]> {
    return Array.from(this.quizzes.values());
  }

  async getQuiz(id: string): Promise<Quiz | undefined> {
    return this.quizzes.get(id);
  }

  async createQuiz(insertQuiz: InsertQuiz): Promise<Quiz> {
    const id = randomUUID();
    const quiz: Quiz = { ...insertQuiz, id, createdAt: new Date() };
    this.quizzes.set(id, quiz);
    return quiz;
  }

  async getQuizResults(userId: string): Promise<QuizResult[]> {
    return Array.from(this.quizResults.values()).filter(result => result.userId === userId);
  }

  async createQuizResult(insertResult: InsertQuizResult): Promise<QuizResult> {
    const id = randomUUID();
    const result: QuizResult = { ...insertResult, id, completedAt: new Date() };
    this.quizResults.set(id, result);
    return result;
  }

  async getLessons(userId: string): Promise<Lesson[]> {
    return Array.from(this.lessons.values()).filter(lesson => 
      lesson.studentId === userId || lesson.teacherId === userId
    );
  }

  async createLesson(insertLesson: InsertLesson): Promise<Lesson> {
    const id = randomUUID();
    const lesson: Lesson = { ...insertLesson, id, createdAt: new Date() };
    this.lessons.set(id, lesson);
    return lesson;
  }

  async updateLesson(id: string, updates: Partial<Lesson>): Promise<Lesson | undefined> {
    const lesson = this.lessons.get(id);
    if (!lesson) return undefined;

    const updatedLesson = { ...lesson, ...updates };
    this.lessons.set(id, updatedLesson);
    return updatedLesson;
  }

  async getResources(): Promise<Resource[]> {
    return Array.from(this.resources.values());
  }

  async createResource(insertResource: InsertResource): Promise<Resource> {
    const id = randomUUID();
    const resource: Resource = { ...insertResource, id, createdAt: new Date() };
    this.resources.set(id, resource);
    return resource;
  }

  async getForumPosts(): Promise<ForumPost[]> {
    return Array.from(this.forumPosts.values());
  }

  async createForumPost(insertPost: InsertForumPost): Promise<ForumPost> {
    const id = randomUUID();
    const post: ForumPost = { ...insertPost, id, replies: 0, createdAt: new Date() };
    this.forumPosts.set(id, post);
    return post;
  }

  async createContactMessage(insertMessage: InsertContactMessage): Promise<ContactMessage> {
    const id = randomUUID();
    const message: ContactMessage = { ...insertMessage, id, status: "new", createdAt: new Date() };
    this.contactMessages.set(id, message);
    return message;
  }
}

// PostgreSQL Storage implementation
export class PostgreSQLStorage implements IStorage {
  private db;

  constructor() {
    if (!process.env.DATABASE_URL) {
      throw new Error("DATABASE_URL environment variable is required");
    }

    console.log("DATABASE_URL format:", process.env.DATABASE_URL?.substring(0, 20) + "...");

    try {
      let connectionString = process.env.DATABASE_URL;

      // Convert HTTPS URL to PostgreSQL URL format if needed
      if (connectionString.startsWith('https://')) {
        console.log("Converting HTTPS URL to PostgreSQL format...");
        // This is likely a Supabase HTTP URL, we need the PostgreSQL connection string instead
        throw new Error("Please provide the PostgreSQL connection string from Supabase, not the HTTP URL. Go to your Supabase project settings > Database > Connection string > PostgreSQL");
      }

      const sql = neon(connectionString);
      this.db = drizzle(sql);

      // Initialize with seed data
      this.initializeDatabase();
    } catch (error) {
      console.error("Database connection error:", error);
      console.log("DATABASE_URL should be in format: postgresql://user:password@host.tld/dbname");
      throw error;
    }
  }

  private async initializeDatabase() {
    try {
      // Run migrations first
      await this.runMigrations();
      // Then seed data
      await this.seedData();
    } catch (error) {
      console.error("Database initialization error:", error);
    }
  }

  private async runMigrations() {
    try {
      // Check if tables exist by trying to select from users table
      await this.db.select().from(users).limit(1);
      console.log("Database tables already exist");
    } catch (error) {
      console.log("Running database migrations...");
      try {
        const migrationPath = path.join(process.cwd(), "migrations", "0000_even_mandarin.sql");
        const migrationSQL = fs.readFileSync(migrationPath, "utf-8");

        // Split SQL statements and execute them
        const statements = migrationSQL
          .split("--> statement-breakpoint")
          .map(stmt => stmt.trim())
          .filter(stmt => stmt.length > 0);

        const sql = neon(process.env.DATABASE_URL!);

        for (const statement of statements) {
          if (statement.trim()) {
            await sql(statement);
          }
        }

        console.log("Database migrations completed successfully");
      } catch (migrationError) {
        console.error("Migration failed:", migrationError);
        throw migrationError;
      }
    }
  }

  private async seedData() {
    try {
      // Check if we have any users
      const existingUsers = await this.db.select().from(users).limit(1);

      if (existingUsers.length === 0) {
        // Create default teacher
        const teacherId = randomUUID();
        const teacher: InsertUser = {
          username: "teacher",
          email: "teacher@grammarmaster.com",
          password: await bcrypt.hash("password", 10),
          firstName: "John",
          lastName: "Smith",
          role: "teacher",
          avatar: null,
        };

        await this.db.insert(users).values({ ...teacher, id: teacherId });

        // Create sample quizzes
        const sampleQuizzes = [
          {
            id: randomUUID(),
            title: "Present Tenses",
            description: "Master the use of simple present, present continuous, and present perfect tenses.",
            difficulty: "beginner",
            category: "tenses",
            timeLimit: 10,
            questions: [
              {
                id: 1,
                question: "She _______ to work every morning at 8 AM.",
                options: ["go", "goes", "going", "went"],
                correctAnswer: 1,
                explanation: "With third person singular (she/he/it) in simple present, we add 's' to the verb."
              },
              {
                id: 2,
                question: "They _______ studying English for two years.",
                options: ["are", "have been", "were", "had been"],
                correctAnswer: 1,
                explanation: "Present perfect continuous shows an action that started in the past and continues to the present."
              }
            ],
          },
          {
            id: randomUUID(),
            title: "Conditional Sentences",
            description: "Learn about zero, first, second, and third conditional structures.",
            difficulty: "intermediate",
            category: "conditionals",
            timeLimit: 15,
            questions: [
              {
                id: 1,
                question: "If it _______ tomorrow, we will stay inside.",
                options: ["rain", "rains", "will rain", "rained"],
                correctAnswer: 1,
                explanation: "In first conditional, we use simple present in the if-clause and will + infinitive in the main clause."
              }
            ],
          },
          {
            id: randomUUID(),
            title: "Advanced Grammar",
            description: "Challenge yourself with complex grammatical structures and rules.",
            difficulty: "advanced",
            category: "advanced",
            timeLimit: 20,
            questions: [
              {
                id: 1,
                question: "_______ the meeting been postponed, we would have had more time to prepare.",
                options: ["Had", "If", "Should", "Were"],
                correctAnswer: 0,
                explanation: "This is an inverted third conditional structure, where 'had' is moved to the beginning instead of using 'if'."
              }
            ],
          }
        ];

        await this.db.insert(quizzes).values(sampleQuizzes);

        // Create sample resources
        const sampleResources = [
          {
            id: randomUUID(),
            title: "Complete Tenses Guide",
            description: "Comprehensive guide covering all English tenses with examples and usage rules.",
            type: "pdf",
            category: "grammar-guide",
            url: "/resources/tenses-guide.pdf",
            isPremium: false,
            fileSize: 2048576,
            duration: null,
          },
          {
            id: randomUUID(),
            title: "Mastering Conditionals",
            description: "Step-by-step video explanation of all conditional sentence types with practical examples.",
            type: "video",
            category: "video-lesson",
            url: "/resources/conditionals-video.mp4",
            isPremium: true,
            fileSize: 52428800,
            duration: 2700,
          },
          {
            id: randomUUID(),
            title: "Article Usage Practice",
            description: "Practice exercises for mastering the correct usage of articles (a, an, the).",
            type: "worksheet",
